import { Data, Layout } from "clarity-decode";

/**
 * @deprecated Use Visualizer instead.
 */
export interface Visualize {
    readonly state: PlaybackState;
    dom: (event: Layout.DomEvent) => Promise<void>;
    html: (decoded: Data.DecodedPayload[], target: Window, hash?: string, time?: number) => Visualize;
    clickmap: (activity?: Activity) => void;
    clearmap: () => void;
    scrollmap: (data?: ScrollMapInfo[], averageFold?: number, addMarkers?: boolean) => void;
    merge: (decoded: Data.DecodedPayload[]) => MergedPayload;
    render: (events: Data.DecodedEvent[]) =>  void;
    setup: (target: Window, options: Options) => Visualize;
    time: () => number;
    get: (hash: string) => HTMLElement;
}

export class Visualizer {
    readonly state: PlaybackState;
    dom: (event: Layout.DomEvent) => Promise<void>;
    html: (decoded: Data.DecodedPayload[], target: Window, hash?: string, useproxy?: LinkHandler, logerror?: ErrorLogger, shortCircuitStrategy?: ShortCircuitStrategy) => Promise<Visualizer>;
    clickmap: (activity?: Activity) => void;
    clearmap: () => void;
    scrollmap: (data?: ScrollMapInfo[], averageFold?: number, addMarkers?: boolean) => void;
    merge: (decoded: Data.DecodedPayload[]) => MergedPayload;
    render: (events: Data.DecodedEvent[]) =>  Promise<void>;
    setup: (target: Window, options: Options) => Promise<Visualizer>;
    time: () => number;
    get: (hash: string) => HTMLElement;
}

export type ResizeHandler  = (width: number, height: number) => void;
export type ErrorLogger = (error: Error) => void;
export type LinkHandler = (link: string, id: string, linkType: string) => string;
export type ClickLogger = (args: IClickLoggerArgs) => void;

export interface IClickLoggerArgs {
    time: number;
    x: number;
    y: number;
    nodeId: number;
}
export interface MergedPayload {
    timestamp: number;
    envelope: Data.Envelope;
    dom: Layout.DomEvent;
    events: Data.DecodedEvent[];
}

export interface Point {
    time: number;
    x: number;
    y: number;
}

export interface Options {
    version: string;
    dom?: Layout.DomEvent;
    onresize?: ResizeHandler;
    logerror?: ErrorLogger;
    useproxy?: LinkHandler;
    onclickMismatch?: ClickLogger;
    metadata?: HTMLElement;
    canvas?: boolean;
    keyframes?: boolean;
    mobile?: boolean;
}

export interface NodeData {
    tag: string;
    parent: number;
    previous: number;
    position?: number;
    alpha?: string;
    beta?: string;
}

export interface PlaybackState {
    window: Window;
    options: Options;
}

export type Activity = ElementData[];

export interface ScrollMapInfo {
    scrollReachY: number;
    cumulativeSum: number;
    percUsers: number;
}

export interface RegionState {
    interaction: Layout.Interaction,
    visibility: Layout.RegionVisibility
}

export interface ElementData {
    hash: string;
    selector: string;
    totalclicks: number;
    x: number[];
    y: number[];
    clicks: number[];
    points: number;
}

export interface Heatmap {
    x: number; /* X Coordinate */
    y: number; /* Y Coordinate */
    a: number; /* Alpha */
}

export const enum ShortCircuitStrategy {
    None = 0,
    HashFirstTimestamp = 1,
    HashFirstTimestampPlusBuffer = 2,
    HashBeforeDeleted = 3
}

export const enum NodeType {
    ELEMENT_NODE = 1,
    ATTRIBUTE_NODE = 2,
    TEXT_NODE = 3,
    COMMENT_NODE = 8,
    DOCUMENT_NODE = 9,
    DOCUMENT_TYPE_NODE = 10
}

